<?phpclass Menu extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_menu integer */    public $id_menu;    /** @var $pagina string */    public $pagina;    /** @var $nome string */    public $nome;    /** @var $icone string */    public $icone;    /** @var $link string */    public $link;    /** @var $permissao string */    public $permissao;    public static function tableName()    {        return "menu";    }    public static function primaryKeyName()    {        return "id_menu";    }    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_menu = $pk;            return $this;        }        return $this->id_menu;    }    public function behaviors()    {        return [];    }    public function reverseBehaviors()    {        return [];    }    public function labels()    {        return [];    }    public function rules()    {        return [];    }    /**     * @return bool | Submenu[]     */    public function getSubmenu()    {        return (new Submenu())->select()->from(['SUBMENU' => Submenu::tableName()])->where(DbDict::EQUAL, ['SUBMENU.id_menu' => $this->primaryKey()])->find();    }}