<?phpuse Winged\Controller\Controller;use Winged\Winged;use Winged\Http\Cookie;use Winged\Http\Session;use Winged\Database\DbDict;class EnderecosClientesController extends Controller{    public function __construct()    {        !Login::permission() ? $this->redirectTo() : null;        parent::__construct();        $this->dynamic('active_page_group', 'enderecos_clientes');        $this->dynamic('page_name', 'EndereÃ§os dos clientes');        $this->dynamic('page_action_string', 'Listando');        $this->dynamic('list', 'enderecos_clientes/page/1');        $this->dynamic('insert', 'enderecos_clientes/insert/');        $this->dynamic('update', 'enderecos_clientes/update/');    }    public function actionIndex()    {        $this->redirectTo(Winged::$page_surname . '/page/1');    }    public function actionPage()    {        AdminAssets::init($this);        $this->setNicknamesToUri(['page', 'id_cliente']);        $this->dynamic('id_cliente', (uri('id_cliente') ? uri('id_cliente') : false));        $limit = get('limit') ? get('limit') : 10;        $page = uri('page') ? uri('page') : 1;        if ($this->id_cliente === false) {            if (($to = Cookie::get('from_url'))) {                Cookie::remove('from_url');                $this->redirectOnly($to);            } else {                $this->redirectTo(Winged::$page_surname);            }        }        $model = new EnderecosClientes();        $success = null;        if (!in_array(Session::get('action'), ['insert', 'update']) && Session::get('action') !== false) {            $success = $model->findOne(Session::get('action'));        }        $success = false;        if (intval(Session::get('action')) > 0) {            $success = true;        }        Session::remove('action');        $links = 1;        $model->select(['BAIRROS.nome', 'CIDADES.cidade', 'ESTADOS.estado', 'ENDERECOS.*'])            ->from(['ENDERECOS' => 'enderecos_clientes'])            ->leftJoin(ELOQUENT_EQUAL, ['ESTADOS' => 'estados', 'ESTADOS.id_estado' => 'ENDERECOS.id_estado'])            ->leftJoin(ELOQUENT_EQUAL, ['CIDADES' => 'cidades', 'CIDADES.id_cidade' => 'ENDERECOS.id_cidade'])            ->leftJoin(ELOQUENT_EQUAL, ['BAIRROS' => 'bairros', 'BAIRROS.id_bairro' => 'ENDERECOS.id_bairro'])            ->where(ELOQUENT_EQUAL, ['ENDERECOS.id_cliente' => $this->id_cliente]);        \Admin::buildSearchModel($model, [            'ESTADOS.estado',            'CIDADES.cidade',            'BAIRROS.nome',            'ENDERECOS.endereco',            'ENDERECOS.numero',            'ENDERECOS.complemento',            'ENDERECOS.codigo_postal',            'ENDERECOS.principal',            'ENDERECOS.data_cadastro',        ], true);        \Admin::buildOrderModel($model, [            'sort_estado' => 'ESTADOS.estado',            'sort_cidade' => 'CIDADES.cidade',            'sort_bairro' => 'BAIRROS.nome',            'sort_endereco' => 'ENDERECOS.endereco',            'sort_numero' => 'ENDERECOS.numero',            'sort_complemento' => 'ENDERECOS.complemento',            'sort_codigo_postal' => 'ENDERECOS.codigo_postal',            'sort_principal' => 'ENDERECOS.principal',            'sort_data_cadastro' => 'ENDERECOS.data_cadastro',        ]);        $paginate = new Paginate($model->count(), $model);        $data = $paginate->getData($limit, $page . '/' . $this->id_cliente);        $links = $paginate->createLinks($links, Winged::$page_surname);        $this->html(Winged::$page_surname . '/' . Winged::$page_surname . '/_index', [            'success' => $success,            'models' => $data->data,            'links' => $links,        ]);    }    public function actionInsert()    {        AdminAssets::init($this);        $this->appendJs('enderecos_clientes', Winged::$parent . 'assets/js/pages/enderecos_clientes.js');        $this->setNicknamesToUri(['id_cliente']);        $this->dynamic('id_cliente', (uri('id_cliente') ? uri('id_cliente') : false));        $this->dynamic('page_action_string', 'Inserindo');        $model = new EnderecosClientes();        $model->id_cliente = $this->id_cliente;        Session::always('action', 'insert');        if (is_get()) {            $this->html(Winged::$page_surname . '/' . Winged::$page_surname . '/_form', [                'model' => $model            ]);        } else if (is_post()) {            $save = $this->save();            if (!$save['status']) {                $this->html(Winged::$page_surname . '/' . Winged::$page_surname . '/_form', [                    'model' => $save['model'],                ]);            }        }    }    public function actionDelete()    {        $model = new EnderecosClientes();        $this->setNicknamesToUri(['id']);        if (uri('id') !== false && is_get()) {            $model->autoLoadDb(uri('id'));            $model->remove();        }        if (($to = Cookie::get('from_url'))) {            Cookie::remove('from_url');            $this->redirectOnly($to);        } else {            $this->redirectTo(Winged::$page_surname);        }    }    public function actionUpdate()    {        AdminAssets::init($this);        $this->appendJs('enderecos_clientes', Winged::$parent . 'assets/js/pages/enderecos_clientes.js');        $this->dynamic('page_action_string', 'Alterando');        $model = new EnderecosClientes();        $this->setNicknamesToUri(['id']);        Session::always('action', 'update');        if (uri('id') !== false && is_get()) {            $model->autoLoadDb(uri('id'));            if ($model->primaryKey()) {                $this->html(Winged::$page_surname . '/' . Winged::$page_surname . '/_form', [                    'model' => $model                ]);            } else {                if (($to = Cookie::get('from_url'))) {                    Cookie::remove('from_url');                    $this->redirectOnly($to);                } else {                    $this->redirectTo(Winged::$page_surname);                }            }        } else if (is_post()) {            $save = $this->save();            if (!$save['status']) {                $this->html(Winged::$page_surname . '/' . Winged::$page_surname . '/_form', [                    'model' => $save['model'],                ]);            }        }    }    public function save()    {        $model = (new EnderecosClientes())->load($_POST);        if ($model->validate() && ($id = $model->save())) {            if ($model->principal == 1) {                (new EnderecosClientes())->update(['ENDERECOS' => 'enderecos_clientes'])->set([                    'principal' => 0,                ])->where(ELOQUENT_EQUAL, ['ENDERECOS.id_cliente' => $model->id_cliente])                    ->andWhere(ELOQUENT_DIFFERENT, ['ENDERECOS.' . EnderecosClientes::primaryKeyName() => $model->primaryKey()])                    ->execute();            }            $action = Session::get('action');            Session::always('action', $id);            if (($to = Cookie::get('from_url')) && $action == 'update') {                Cookie::remove('from_url');                $this->redirectOnly($to);            } else {                $this->redirectTo(Winged::$page_surname);            }        } else {            return ['status' => false, 'model' => $model];        }    }}