<?phpuse Winged\Model\Model;/** * Class Carrinho * * @package Winged\Model */class Carrinho extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_carrinho integer */    public $id_carrinho;    /** @var $id_usuario integer */    public $id_usuario;    /** @var $id_produto integer */    public $id_produto;    /** @var $quantidade int */    public $quantidade;    /** @var $valor_unico integer */    public $valor_unico;    /** @var $porcentagem_desconto integer */    public $porcentagem_desconto;    /**     * @return string     */    public static function tableName()    {        return "carrinho";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_carrinho";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_carrinho = $pk;            return $this;        }        return $this->id_carrinho;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [];    }    /**     * @return array     */    public function rules()    {        return [];    }    /**     * @return array     */    public function messages()    {        return [];    }}