<?phpnamespace Winged\Model;class Estados extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_estado integer */    public $id_estado;    /** @var $id_pais integer */    public $id_pais = 1;    /** @var $estado string */    public $estado;    /** @var $uf string */    public $uf;    public static function tableName()    {        return "estados";    }        public static function primaryKeyName()    {        return "id_estado";    }        public function primaryKey($pk = false)    {        if($pk && (is_int($pk) || intval($pk) != 0)){            $this->id_estado = $pk;            return $this;        }        return $this->id_estado;    }        public function behaviors()    {        return [];    }        public function reverseBehaviors()    {        return [];    }        public function labels()    {        return [            'estado' => 'Nome do estado',            'uf' => 'UF',        ];    }        public function rules()    {        return [];    }    public function messages()    {        return [];    }}