<?phpuse Winged\Model\Model;/** * Class Estados */class Estados extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_estado integer */    public $id_estado;    /** @var $id_pais integer */    public $id_pais = 1;    /** @var $estado string */    public $estado;    /** @var $uf string */    public $uf;    /**     * @return string     */    public static function tableName()    {        return "estados";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_estado";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_estado = $pk;            return $this;        }        return $this->id_estado;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [            'estado' => 'Nome do estado',            'uf' => 'UF',        ];    }    /**     * @return array     */    public function rules()    {        return [];    }    /**     * @return array     */    public function messages()    {        return [];    }}