<?phpuse Winged\Model\Model;use Winged\Database\DbDict;/** * Class Menu */class Menu extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_menu integer */    public $id_menu;    /** @var $pagina string */    public $pagina;    /** @var $nome string */    public $nome;    /** @var $icone string */    public $icone;    /** @var $link string */    public $link;    /** @var $permissao string */    public $permissao;    /**     * @return string     */    public static function tableName()    {        return "menu";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_menu";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_menu = $pk;            return $this;        }        return $this->id_menu;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [];    }    /**     * @return array     */    public function rules()    {        return [];    }    /**     * @return array     */    public function messages()    {        return [];    }    /**     * @return bool | Submenu[]     */    public function getSubmenu()    {        return (new Submenu())->select()->from(['SUBMENU' => Submenu::tableName()])->where(ELOQUENT_EQUAL, ['SUBMENU.id_menu' => $this->primaryKey()])->execute();    }}