<?phpuse Winged\Model\Model;use Winged\Formater\Formater;use Winged\Database\DbDict;/** * Class NewsCategorias */class NewsCategorias extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_categoria integer */    public $id_categoria;    /** @var $categoria string */    public $categoria;    /**     * @return string     */    public static function tableName()    {        return "news_categorias";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_categoria";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_categoria = $pk;            return $this;        }        return $this->id_categoria;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [            'categoria' => 'Nome da categoria: ',        ];    }    /**     * @return array     */    public function rules()    {        return [            'categoria' => [                'required' => true,                'db' => Slugs::exists($this->categoria)            ]        ];    }    /**     * @return array     */    public function messages()    {        return [            'categoria' => [                'required' => 'Esse campo é obrigatório',                'db' => 'Esse nome já é utilizado por outro registro dentro do banco de dados'            ]        ];    }}