<?phpuse Winged\Model\Model;class PedidosProdutos extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_pedido_produto integer */    public $id_pedido_produto;    /** @var $id_pedido integer */    public $id_pedido;    /** @var $id_produto integer */    public $id_produto;    /** @var $nome string */    public $nome;    /** @var $valor_unico integer */    public $valor_unico;    /** @var $quantidade integer */    public $quantidade;    /** @var $origem_valor integer */    public $origem_valor;    /** @var $porcentagem_desconto integer */    public $porcentagem_desconto;    /** @var $fix_custo integer */    public $fix_custo;    public static function tableName()    {        return "pedidos_produtos";    }        public static function primaryKeyName()    {        return "id_pedido_produto";    }        public function primaryKey($pk = false)    {        if($pk && (is_int($pk) || intval($pk) != 0)){            $this->id_pedido_produto = $pk;            return $this;        }        return $this->id_pedido_produto;    }        public function behaviors()    {        return [];    }        public function reverseBehaviors()    {        return [];    }        public function labels()    {        return [];    }        public function rules()    {        return [];    }    public function messages()    {        return [];    }}