<?phpnamespace Winged\Model;class ProdutosCategorias extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_categoria integer */    public $id_categoria;    /** @var $id_usuario integer */    public $id_usuario;    /** @var $nome string */    public $nome;    public static function tableName()    {        return "produtos_categorias";    }        public static function primaryKeyName()    {        return "id_categoria";    }        public function primaryKey($pk = false)    {        if($pk && (is_int($pk) || intval($pk) != 0)){            $this->id_categoria = $pk;            return $this;        }        return $this->id_categoria;    }        public function behaviors()    {        return [];    }        public function reverseBehaviors()    {        return [];    }        public function labels()    {        return [            'nome' => 'Nome da categoria: '        ];    }        public function rules()    {        return [            'nome' => [                'required' => true,            ]        ];    }    public function messages()    {        return [            'nome' => [                'required' => 'Esse campo é obrigatório',            ]        ];    }}