<?phpuse Winged\Model\Model;/** * Class ProdutosCategorias */class ProdutosCategorias extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_categoria integer */    public $id_categoria;    /** @var $id_usuario integer */    public $id_usuario;    /** @var $nome string */    public $nome;    /**     * @return string     */    public static function tableName()    {        return "produtos_categorias";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_categoria";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_categoria = $pk;            return $this;        }        return $this->id_categoria;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [            'nome' => 'Nome da categoria: '        ];    }    /**     * @return array     */    public function rules()    {        return [            'nome' => [                'required' => true,            ]        ];    }    /**     * @return array     */    public function messages()    {        return [            'nome' => [                'required' => 'Esse campo é obrigatório',            ]        ];    }}