<?phpnamespace Winged\Model;class ProdutosSubcategorias extends Model{    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_subcategoria integer */    public $id_subcategoria;    /** @var $id_usuario integer */    public $id_usuario;    /** @var $id_categoria integer */    public $id_categoria;    /** @var $nome string */    public $nome;    public static function tableName()    {        return "produtos_subcategorias";    }        public static function primaryKeyName()    {        return "id_subcategoria";    }        public function primaryKey($pk = false)    {        if($pk && (is_int($pk) || intval($pk) != 0)){            $this->id_subcategoria = $pk;            return $this;        }        return $this->id_subcategoria;    }        public function behaviors()    {        return [];    }        public function reverseBehaviors()    {        return [];    }        public function labels()    {        return [            'nome' => 'Nome da subcategoria: ',            'id_categoria' => 'Categoria a que pertence: ',        ];    }        public function rules()    {        return [            'nome' => [                'required' => true,            ],            'id_categoria' => [                'required' => true,            ],        ];    }    public function messages()    {        return [            'nome' => [                'required' => 'Esse campo é obrigatório',            ],            'id_categoria' => [                'required' => 'Esse campo é obrigatório',            ],        ];    }}