<?phpuse Winged\Model\Model;/** * Class ProdutosSubcategorias */class ProdutosSubcategorias extends Model{    /**     * ProdutosSubcategorias constructor.     */    public function __construct()    {        parent::__construct();        return $this;    }    /** @var $id_subcategoria integer */    public $id_subcategoria;    /** @var $id_usuario integer */    public $id_usuario;    /** @var $id_categoria integer */    public $id_categoria;    /** @var $nome string */    public $nome;    /**     * @return string     */    public static function tableName()    {        return "produtos_subcategorias";    }    /**     * @return string     */    public static function primaryKeyName()    {        return "id_subcategoria";    }    /**     * @param bool $pk     *     * @return $this|int|Model     */    public function primaryKey($pk = false)    {        if ($pk && (is_int($pk) || intval($pk) != 0)) {            $this->id_subcategoria = $pk;            return $this;        }        return $this->id_subcategoria;    }    /**     * @return array     */    public function behaviors()    {        return [];    }    /**     * @return array     */    public function reverseBehaviors()    {        return [];    }    /**     * @return array     */    public function labels()    {        return [            'nome' => 'Nome da subcategoria: ',            'id_categoria' => 'Categoria a que pertence: ',        ];    }    /**     * @return array     */    public function rules()    {        return [            'nome' => [                'required' => true,            ],            'id_categoria' => [                'required' => true,            ],        ];    }    /**     * @return array     */    public function messages()    {        return [            'nome' => [                'required' => 'Esse campo é obrigatório',            ],            'id_categoria' => [                'required' => 'Esse campo é obrigatório',            ],        ];    }}